def reverseString(x):
  return x[::-1]


def subString(s):
    result=""
    for i in range(1, len(s)+1):
        for j in range(len(s) - i + 1):
            k = j + i - 1
            temp = ""
            for l in range(j, k + 1):
                temp = temp+s[l]
            if temp == reverseString(temp):
                result=temp
    print(result)


